import {useEffect,useState} from 'react';

import {Table,TableHead,TableRow,TableCell, TableBody,styled, Button} from '@mui/material';

import {getUsers} from '../service/api';
import {Link} from 'react-router-dom';


const StyledTable = styled(Table)`
    width:90%;
    margin:50px auto 0 auto;
`;

const Thead = styled(TableRow)`
    background:#000000;
    & > th{
        color:#fff;
        font-size:20px;
    }
`;

const Tbody = styled(TableRow)`
& > td {
    font-size:20px;
}
`



const AllUsers = () =>{

    const [users,setUsers] =useState([]);

    useEffect(()=>{
        getAllUsers();
    },[]);

const getAllUsers = async() =>{
   let response= await getUsers();
   setUsers(response.data);
 
} 



    return(
        <StyledTable>
            <TableHead>
                <Thead>
                    <TableCell>Id</TableCell>
                    <TableCell>Name</TableCell>
                    <TableCell>Roll</TableCell>
                    <TableCell>Username</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell>Phone</TableCell>
                    <TableCell></TableCell>
                </Thead>
            </TableHead>
            <TableBody>
                {
                    users.map(user =>(
                        <Tbody>
                            <TableCell>{user._id}</TableCell>
                            <TableCell>{user.name}</TableCell>
                            <TableCell>{user.Roll}</TableCell>
                            <TableCell>{user.username}</TableCell>
                            <TableCell>{user.email}</TableCell>
                            <TableCell>{user.phone}</TableCell>
                            <TableCell>
                                <Button variant="contained" style={{marginRight:15}} component={Link} to={`/edit/${user._id}`}>Edit</Button>
                                <Button variant="contained" color="secondary" onClick={()=>deleteUser(user._id) } >Delete</Button>
                            </TableCell>
                        </Tbody>
                    ))
                }
            </TableBody>
        </StyledTable>
    )
}

export default AllUsers;
